@-moz-document domain("auth0.openai.com") {
  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor);
    }
  }

  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    #lib.palette();

    // TODO: Can we replace this with defaults? Why is input/textarea placeholder left out?
    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    --primary-color: @accent;
    --primary-color-no-override: @accent;
    --action-primary-color: @accent;
    --link-color: @accent;
    --page-background-color: @base;
    --info-color: @blue;
    --success-color: @green;
    --error-color: @red;
