      --warm-gray-30: @base;
      --warm-gray-40: @base;
      --warm-gray-50: @base;
      --warm-gray-60: @base;
      --warm-gray-70: @base;
      --warm-gray-80: @base;
      --warm-gray-90: @base;
      --warm-gray-100: @base;
      --warm-gray-60-a10: fade(@base, 10%);
      --warm-gray-60-a15: fade(@base, 15%);
